% Design and analysis script for ME 4550 final design project
%
% Matthew Bonanni, Nicolas Iacovelli, Adrian Kombe, Ryan Loehr, Becca Sung

d_A = 24;

d_B = 10;

n = 3;

Sy = 71000;

Sut = 85000;

PressureAngle = 20;

P_A = 600;

OA = 20;

AB = 16;

BC = 10;

% Calculations of the resultant forces in X&Y directiosn on A and B

C_z = cos(PressureAngle) * P_A;

C_y = sin(PressureAngle) * (-P_A);

A_z = sin(PressureAngle) * (-P_A);

A_y = cos(PressureAngle) * (P_A);

% The torque generated by gears A&C is equal and opposite and thus from A -> C there is a consistent torque of

T = P_A * (d_A/2);

B_y = -1 * ((OA * A_y) - (C_y * (OA + AB + BC))/(OA + AB));

O_y = -1 * (A_y - C_y + B_y);

B_z = ((-C_z * (OA + AB + BC)) + (A_z * OA)) / (OA + AB);

O_z = B_z - A_z + C_z;

M_ay = O_y * OA;

M_az = O_z * OA;

M_a_tot = sqrt((M_ay^2) + (M_az)^2);

M_by = C_y * BC;

M_bz = A_z * BC;

M_b_tot = sqrt((M_by^2)+(M_bz^2));